tmpls.qfmt:
{{#IsRepeated}}
<span style="font-size: 20px" id="word">{{Vocabulary-English}}</span>
<script>
var word = document.getElementById("word");
var bold =  word.getElementsByTagName("b");
if(bold.length>0){
	var newHTML = "";
	for(var i=0;i<bold.length;i++) {
  		newHTML += bold[i].innerHTML;
	}
	word.innerHTML = newHTML;
}
if("{{Language}}" == "Chinese") {
	word.style.backgroundColor = '#defeae';
}
</script>
{{/IsRepeated}}

{{#Definition}}{{#EnableJJProduction}}
<span style="font-size: 20px" id="word"></span>
<br>
<span class='exampleSentences'>
{{Example-Sentences}}
</span>
<script>
var word = (function () {/*{{Definition}}*/}).toString().slice(15,-4).split(/(<br>|<br \/>)/g);
var html = "";
for(var w in word) {
  if(
word[w].indexOf("（形）")==-1 && 
word[w].indexOf("［文］")==-1 && 
word[w].indexOf("と同源")==-1 && 
word[w].indexOf("」の転")==-1 && 
word[w].indexOf("」の慣用読み")==-1 &&
word[w].indexOf("［可能］")==-1 &&
word[w].indexOf("」とも")==-1 &&
word[w].indexOf("の訳語〕")==-1
)
  	html += word[w];
}
document.getElementById("word").innerHTML = html;
var red = document.getElementsByClassName("redtext");
for(var i in red) {
  red[i].innerHTML = '___';
}
function shuffle(array) {
  var currentIndex = array.length, temporaryValue, randomIndex;
  while (0 !== currentIndex) {
    randomIndex = Math.floor((new Date().getDate()*1337) % currentIndex);
    currentIndex -= 1;
    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }

  return array;
}
var li = shuffle([].slice.call(document.querySelectorAll(".exampleSentences>ul>li")));
for(var i=0;i<li.length;++i) {
  if(i<10)
    li[i].style.display = 'initial';
  else
    li[i].style.display = 'none';
}
</script>
{{/EnableJJProduction}}{{/Definition}}
